import React, { Fragment } from "react";
import { NavLink } from "react-router-dom";

import classes from "./nav.module.css";
import GlobalContext from "../../store/globalContext";

const Navigation = () => {
  function activeStyle(type) {
    if (type === "list") {
      return classes.list_nav + " " + classes.active;
    } else {
      return classes.form_nav + " " + classes.active;
    }
  }

  return (
    <GlobalContext.Consumer>
      {(ctx) => {
        return (
          <Fragment>
            <NavLink
              className={
                ctx.mode === "list" ? activeStyle("list") : classes.list_nav
              }
              to="/list"
            >
              List
            </NavLink>

            <NavLink
              // className={({ isActive }) =>
              //   isActive ? activeStyle("form") : classes.form_nav
              // }
              className={
                ctx.mode === "form" ? activeStyle("form") : classes.form_nav
              }
              to="/new-form"
            >
              Form
            </NavLink>
          </Fragment>
        );
      }}
    </GlobalContext.Consumer>
  );
};

export default Navigation;
