import React, { useRef, useState } from "react";
import { NavLink } from "react-router-dom";

import classes from "./form.module.css";

const Form = () => {
  const [enteredEventName, setEnteredEventName] = useState("");

  nameInputChangeHandler = (event) => {
    setEnteredEventName(event.target.value);
  };

  return (
    <form className={classes.cont_form}>
      <label className={classes.col}>
        Name:
        <input type="text" id="name" name="name" placeholder="Event Name..." />
      </label>

      <label className={classes.col}>
        Description:
        <textarea
          id="text-area"
          name="text-area"
          placeholder="Details about the event..."
        ></textarea>
      </label>

      <label className={classes.col}>
        Date:
        <input type="date" id="date" name="date" />
      </label>
      <div className={classes.col}>
        <NavLink className={classes.cancel} to="/">
          Cancel
        </NavLink>
        <input type="submit" value="Add" id="add-save" disabled />
      </div>
    </form>
  );
};

export default Form;
