import React, { useState, useRef } from "react";
import { NavLink } from "react-router-dom";

import classes from "./form.module.css";

const Form = (props) => {
  const [isActive, setIsActive] = useState(false);

  const nameInputRef = useRef();
  const dateInputRef = useRef();
  const descriptionInputRef = useRef();

  const formSubmissionHandler = (event) => {
    event.preventDefault();
    let enteredName = nameInputRef.current.value;
    let enteredDescription = descriptionInputRef.current.value;
    let enteredDate = dateInputRef.current.value;

    console.log(enteredName);
    console.log(enteredDescription);
    console.log(enteredDate);
  };

  const nameChangeHandler = (event) => {
    checkActive();
  };

  const dateChangeHandler = (event) => {
    checkActive();
  };

  function checkActive() {
    if (nameInputRef.current.value && dateInputRef.current.value) {
      setIsActive(true);
    } else {
      setIsActive(false);
    }
  }

  return (
    <form className={classes.cont_form} onSubmit={formSubmissionHandler}>
      <label className={classes.col}>
        Name:
        <input
          type="text"
          id="name"
          name="name"
          placeholder="Event Name..."
          ref={nameInputRef}
          required
          onChange={nameChangeHandler}
        />
      </label>

      <label className={classes.col}>
        Description:
        <textarea
          id="text-area"
          name="text-area"
          placeholder="Details about the event..."
          ref={descriptionInputRef}
        ></textarea>
      </label>

      <label className={classes.col}>
        Date:
        <input
          type="date"
          id="date"
          name="date"
          ref={dateInputRef}
          required
          onChange={dateChangeHandler}
        />
      </label>
      <div className={classes.col}>
        <NavLink className={classes.cancel} to="/">
          Cancel
        </NavLink>
        <input type="submit" value="Add" id="add-save" disabled={!isActive} />
      </div>
    </form>
  );
};

export default Form;
