import React, { useState, useRef, useContext, useEffect } from "react";
import { NavLink, useNavigate, useParams } from "react-router-dom";

import classes from "./form.module.css";
import GlobalContext from "../../../store/globalContext";

const Form = (props) => {
  const [saveMode, setSaveMode] = useState(false);
  const [isValid, setIsValid] = useState(false);

  const ctx = useContext(GlobalContext);
  let { id } = useParams();
  var event;

  useEffect(() => {
    if (id) {
      setSaveMode(true);
    } else {
      setSaveMode(false);
    }
  }, [id]);

  if (saveMode) {
    event = ctx.events.find((obj) => {
      return obj.id === id;
    });
  }

  let navigate = useNavigate();
  const routeChange = () => {
    let path = "/list";
    navigate(path);
  };

  const nameInputRef = useRef();
  const dateInputRef = useRef();
  const descriptionInputRef = useRef();

  const formSubmissionHandler = (event) => {
    event.preventDefault();

    const obj = {
      name: nameInputRef.current.value,
      description: descriptionInputRef.current.value,
      date: dateInputRef.current.value,
    };
    addEventHandler(obj);
  };

  function addEventHandler(obj) {
    fetch("https://event-task-f2694-default-rtdb.firebaseio.com/events.json", {
      method: "POST",
      body: JSON.stringify(obj),
      headers: {
        "Content-Type": "application/json",
      },
    }).then(() => {
      routeChange();
    });
  }

  const nameChangeHandler = (event) => {
    checkValid();
  };

  const dateChangeHandler = (event) => {
    checkValid();
  };

  function checkValid() {
    if (nameInputRef.current.value && dateInputRef.current.value) {
      setIsValid(true);
    } else {
      setIsValid(false);
    }
  }

  return (
    <form className={classes.cont_form} onSubmit={formSubmissionHandler}>
      <label className={classes.col}>
        Name:
        <input
          type="text"
          id="name"
          name="name"
          placeholder="Event Name..."
          value={event.name}
          ref={nameInputRef}
          required
          onChange={nameChangeHandler}
        />
      </label>

      <label className={classes.col}>
        Description:
        <textarea
          id="text-area"
          name="text-area"
          placeholder="Details about the event..."
          value={event.description}
          ref={descriptionInputRef}
        ></textarea>
      </label>

      <label className={classes.col}>
        Date:
        <input
          type="date"
          id="date"
          name="date"
          value={event.date}
          ref={dateInputRef}
          required
          onChange={dateChangeHandler}
        />
      </label>
      <div className={classes.col}>
        <NavLink className={classes.cancel} to="/">
          Cancel
        </NavLink>
        <input type="submit" value="Add" id="add-save" disabled={!isValid} />
      </div>
    </form>
  );
};

export default Form;
