import React, { useEffect } from "react";
import { NavLink } from "react-router-dom";

import classes from "./item.module.css";

const Item = (props) => {
  let style = classes.item;

  // useEffect(() => {
  if (setColor(props.obj.date) === 0) {
    style += " " + classes.present;
  } else if (setColor(props.obj.date) === -1) {
    style += " " + classes.future;
  }
  // }, [style]);

  return (
    <NavLink className={style} to={"/edit-form/" + props.obj.id}>
      <h5>{props.obj.name}</h5>
      <p>{props.obj.date}</p>
    </NavLink>
  );
};

function setColor(date) {
  var dateObj = new Date();
  var month = dateObj.getUTCMonth() + 1; //months from 1-12
  var day = dateObj.getUTCDate();
  var year = dateObj.getUTCFullYear();
  const currDate = new Date(year, month, day).getTime();
  const formatDate = new Date(date).getTime();
  console.log(currDate + "//" + formatDate);
  console.log(currDate === formatDate);
  if (currDate < formatDate) {
    return -1;
  } else if (currDate === formatDate) {
    return 0;
  } else {
    return 1;
  }
}

export default Item;
