import React, { Fragment, useState } from "react";
import { BrowserRouter, Routes, Route } from "react-router-dom";

import Header from "./components/headerComponents/header";
import Navigation from "./components/navComponents/Navigation";
import Container from "./components/mainContainer/Container";

import "./App.css";

const App = () => {
  const [mode, setMode] = useState("list");

  const headerAndNav = (
    <Fragment>
      <Header />
      <Navigation />
    </Fragment>
  );

  return (
    <BrowserRouter>
      <Routes>
        <Route
          path="/"
          element={
            <Fragment>
              {headerAndNav}
              <Container mode={"list"} />
            </Fragment>
          }
        />
        <Route
          path="/list"
          element={
            <Fragment>
              {headerAndNav}
              <Container mode={"list"} />
            </Fragment>
          }
        />

        <Route
          path="/form"
          element={
            <Fragment>
              {headerAndNav}
              <Container mode={"form"} />
            </Fragment>
          }
        />
      </Routes>
    </BrowserRouter>
  );
};

export default App;
