import React, { useState, useRef } from "react";
import { Route, BrowserRouter } from "react-router-dom";
import { NavLink } from "react-router-dom";

import classes from "./form.module.css";
import LoadingSpinner from "../../loadingComponent/LoadingSpinner";

const Form = (props) => {
  const [isActive, setIsActive] = useState(false);

  const nameInputRef = useRef();
  const dateInputRef = useRef();
  const descriptionInputRef = useRef();

  const formSubmissionHandler = (event) => {
    event.preventDefault();

    const obj = {
      name: nameInputRef.current.value,
      description: descriptionInputRef.current.value,
      date: dateInputRef.current.value,
    };
    addEventHandler(obj);
  };

  function addEventHandler(obj) {
    fetch("https://event-task-f2694-default-rtdb.firebaseio.com/events.json", {
      method: "POST",
      body: JSON.stringify(obj),
      headers: {
        "Content-Type": "application/json",
      },
    }).then(
      <BrowserRouter>
        <LoadingSpinner />
        <Route path="*" element={<Navigate to="/" replace />} />
      </BrowserRouter>
    );
  }

  const nameChangeHandler = (event) => {
    checkActive();
  };

  const dateChangeHandler = (event) => {
    checkActive();
  };

  function checkActive() {
    if (nameInputRef.current.value && dateInputRef.current.value) {
      setIsActive(true);
    } else {
      setIsActive(false);
    }
  }

  return (
    <form className={classes.cont_form} onSubmit={formSubmissionHandler}>
      <label className={classes.col}>
        Name:
        <input
          type="text"
          id="name"
          name="name"
          placeholder="Event Name..."
          ref={nameInputRef}
          required
          onChange={nameChangeHandler}
        />
      </label>

      <label className={classes.col}>
        Description:
        <textarea
          id="text-area"
          name="text-area"
          placeholder="Details about the event..."
          ref={descriptionInputRef}
        ></textarea>
      </label>

      <label className={classes.col}>
        Date:
        <input
          type="date"
          id="date"
          name="date"
          ref={dateInputRef}
          required
          onChange={dateChangeHandler}
        />
      </label>
      <div className={classes.col}>
        <NavLink className={classes.cancel} to="/">
          Cancel
        </NavLink>
        <input type="submit" value="Add" id="add-save" disabled={!isActive} />
      </div>
    </form>
  );
};

export default Form;
