import React, { Fragment, useContext } from "react";
import { NavLink } from "react-router-dom";

import classes from "./nav.module.css";

const Navigation = () => {
  const toggleNav = useContext(NavContext);

  function activeStyle(type) {
    if (type === "list") {
      return classes.list_button + " " + classes.active;
    } else {
      return classes.form_button + " " + classes.active;
    }
  }

  return (
    <NavContext.Provider>
      <nav className={classes.list_nav}>
        <NavLink
          className={({ isActive }) =>
            isActive ? activeStyle("list") : classes.list_button
          }
          to="/list"
        >
          List
        </NavLink>
      </nav>

      <nav className={classes.form_nav}>
        <NavLink
          className={({ isActive }) =>
            isActive ? activeStyle("form") : classes.form_button
          }
          to="/new-form"
        >
          Form
        </NavLink>
      </nav>
    </NavContext.Provider>
  );
};

export default Navigation;
